}输出结果四、队列的链式结构设立一个队首指针front ，一个队尾指针rear ，分别指向队首和队尾元素。◆ 初始化：front=rear=pHead。◆ 队列为空：front=NULL。◆ 队满：无。◆ 入队：新建节点，将新节点插入rear所指的下一个位置，修改rear指针。◆ 出队：删去front所指的下一个节点，然后指向下下一个节点，最后释放。◆ 取队首元素：返回fornt指向的元素值//当不需要修改队列中的元素时，建议不要直接用指针形参操作
#include &lt;stdio.h&gt;
#include &lt;stdlib.h&gt; //使用malloc()的库 
#include &lt;assert.h&gt; //使用断言：assert(LQ)

typedef int elemtype;

typedef struct NodeType 
{
    elemtype data;
    struct NodeType* next;
}Node;

typedef struct LinkQueueType
{
    Node* front;
    Node* rear;
}LinkQueue;


//初始化队列
void InitLinkQueue(LinkQueue* LQ)
{
    //创建一个头结点
    Node* pHead = (Node*)malloc(sizeof(Node));
    assert(pHead);
    LQ-&gt;front = LQ-&gt;rear = pHead; //队头和队尾指向头结点
    LQ-&gt;front-&gt;next = NULL;
}

//判断队列是否为空
int IsEmpty(LinkQueue LQ)
{
    if (LQ.front-&gt;next == NULL)
    {
        return 1;
    }
    return 0;
}

//入队操作
void EnterLinkQueue(LinkQueue* LQ, elemtype data)
{
    //创建一个新结点
    Node* pNewNode = (Node*)malloc(sizeof(Node));
    assert(pNewNode);
    pNewNode-&gt;data = data;  //将数据元素赋值给结点的数据域
    pNewNode-&gt;next = NULL;  //将结点的指针域置空
    LQ-&gt;rear-&gt;next = pNewNode;   //将原来队列的队尾指针指向新结点
    LQ-&gt;rear = pNewNode;      //将队尾指针指向新结点
}

//出队操作
int DeleteLinkQueue(LinkQueue* LQ,elemtype* data)
{
    if (IsEmpty(*LQ))
    {
        printf(&quot;队列为空！\n&quot;);
        return 0;
    }
//pDel指向队头元素，由于队头指针front指向头结点，所以pDel指向头结点的下一个结点
    Node* pDel = LQ-&gt;front-&gt;next;  
    *data = pDel-&gt;data;   //将要出队的元素赋给data
    LQ-&gt;front-&gt;next = pDel-&gt;next;  //使指向头结点的指针指向pDel的下一个结点

//如果队列中只有一个元素,将队列置空
    if (pDel-&gt;next == NULL)   
    {
        LQ-&gt;rear = LQ-&gt;front;
    }
    free(pDel);   //释放pDel指向的空间
    return 1;
}

//取队头元素
int GetHead(LinkQueue LQ, elemtype* data)
{
    if (IsEmpty(LQ))
    {
        printf(&quot;队列为空!\n&quot;);
        return 0;
    }
    *data =  LQ.front-&gt;next-&gt;data;   //将队头元素值赋给data
    return 1; 
}

//清空队列
void ClearQueue(LinkQueue* LQ)
{
    while (LQ-&gt;front != NULL)
    {
        LQ-&gt;rear = LQ-&gt;front-&gt;next;  //队尾指针指向队头指针的下一个结点
        free(LQ-&gt;front);              //释放队头指针指向的结点
        LQ-&gt;front = LQ-&gt;rear;         //队头指针指向队尾指针
    }
}

//打印队列中的元素
void PrintLinkQueue(LinkQueue LQ)
{ 
    Node *pCur= LQ.front-&gt;next;;
    assert(&amp;LQ);
    while (pCur)
    {
        printf(&quot;%-3d&quot;, pCur-&gt;data);
        pCur = pCur-&gt;next;
    }
    printf(&quot;\n&quot;);
}
int main()
{
    LinkQueue LQ; 
    elemtype data;
//初始化队列。如果没有初始化，则需要在定义LQ时(即第一条语句)使用关键字“static ”
    InitLinkQueue(&amp;LQ);

    EnterLinkQueue(&amp;LQ, 1); 
    EnterLinkQueue(&amp;LQ, 2);
    EnterLinkQueue(&amp;LQ, 3);
    EnterLinkQueue(&amp;LQ, 4);
    EnterLinkQueue(&amp;LQ, 5);
    EnterLinkQueue(&amp;LQ, 6);
    EnterLinkQueue(&amp;LQ, 7);
    EnterLinkQueue(&amp;LQ, 8);

    printf(&quot;队列中的元素为：&quot;);
    PrintLinkQueue(LQ);
    printf(&quot;\n&quot;);

    data = GetHead(LQ, &amp;data);
    printf(&quot;队头元素为：%d\n&quot;, data);
    printf(&quot;\n&quot;);

    DeleteLinkQueue(&amp;LQ, &amp;data);
    printf(&quot;出队的元素为：%d\n&quot;, data);
    printf(&quot;\n&quot;);

    printf(&quot;队列中的元素为：&quot;);
    PrintLinkQueue(LQ);
    printf(&quot;\n&quot;);

    GetHead(LQ, &amp;data);
    printf(&quot;队头元素为：%d\n&quot;, data);
    printf(&quot;\n&quot;);

    ClearQueue(&amp;LQ);

    return 1;
